import React, {Component} from 'react';
import axios from 'axios';

const TYPE_COLORS = {
  bug: 'B1C12E',
    dark: '4E3A2D',
    dragon: '755EDF'
};


class Pokemon extends Component {
    state = {
      name: '',
      pokemonIndex: '',
      imageUrl: '',
      types: [],
      description: '',
      stats: {
          hp: "",
          attack: "",
          defense: "",
          speed: "",
          specialAttack: "",
          specialDefense: ""
      },
        height: "",
        weight: "",
        eggGroup: "",
        abilities: "",
        genderRatioMale: "",
        genderRatioFemale: "",
        evs:"",
        hatchSteps: ""
    };


    async componentDidMount() {
        //eslint-disable-next-line
        const {pokemonIndex} = this.props.match.params;
        const pokemonUrl = `https://pokeapi.co/api/v2/pokemon/${pokemonIndex}/`;
        const pokemonSpeciesUrl = `https://pokeapi.co/api/v2/pokemon-species/${pokemonIndex}/`;
        const pokemonResponse = await axios.get(pokemonUrl);
        const imageUrl = pokemonResponse.data.sprites.front_default;
        const name = pokemonResponse.data.name;
        let { hp, attack, defense, speed, specialAttack, specialDefense } = '';
        pokemonResponse.data.stats.map(stat => {
            switch (stat.stat.name) {
                case 'hp':
                    hp = stat['base_stat'];
                    break;
                case 'attack':
                    attack = stat['base_stat'];
                    break;
                case 'defense':
                    defense = stat['base_stat'];
                    break;
                case 'speed':
                    speed = stat['base_stat'];
                    break;
                case 'special-attack':
                    specialAttack = stat['base_stat'];
                    break;
                case 'special-defense':
                    specialDefense = stat['base_stat'];
                    break;
            }
        });
        this.setState({name: name});

        const height = Math.round((pokemonResponse.data.height * 0.328084 * 0.0001) * 100) / 100;
        const weight = Math.round((pokemonResponse.data.weight * 0.220462 * 0.0001) * 100) / 100;
        const types = pokemonResponse.data.types.map(type => type.type.name);

        const abilities = pokemonResponse.data.abilities.map((ability) => {
            return ability.ability.name.toLowerCase().split("-")
                .map(s => s.charAt(0).toUpperCase() + s.substring(1)).join(" ");
        });

        const evs = pokemonResponse.data.stats.filter(stat => {
            if (stat.effort > 0) {
                return true;
            }
            return  false;
        }).map(stat => {
           return `${stat.effort} ${stat.stat.name}`
               .toLowerCase().split("-")
               .map(s => s.charAt(0).toUpperCase() + s.substring(1))
               .join(" ");
        }).join(', ');

        await axios.get(pokemonSpeciesUrl).then(res => {
            let description = '';
            res.data.flavor_text_entries.some(flavor => {
               if (flavor.language.name === 'en') {
                   description = flavor.flavor_text;
                   return;
               }
            });
            const femaleRate = res.data['gender_rate'];
            const genderRatioFemale = 12.5 * femaleRate;
            const genderRatioMale = 12.5 * (8 - femaleRate);
            const catchRate = Math.round((100 / 255) * res.data['capture_rate']);
            const eggGroups = res.data['egg_groups'].map(group => {
                return group.name.toLowerCase().split("-")
                    .map(s => s.charAt(0).toUpperCase() + s.substring(1)).join(" ");
            }).join(', ');

            const hatchSteps = 255 * (res.data['hatch_counter'] + 1);

            this.setState({
                description,
                genderRatioFemale,
                genderRatioMale,
                catchRate,
                eggGroups,
                hatchSteps
            });
        });

        this.setState({
           imageUrl,
           pokemonIndex,
           name,
           types,
            stats: {
                hp,
                attack,
                specialDefense,
                specialAttack,
                speed,
                defense
            },
            height,
            weight,
            abilities,
            evs
        });


    }

    render() {
        return (
            <div className="col">
                <div className="card">
                    <div className="card-header">
                        <div className="row">
                            <div className="col-5">
                                <h5>{this.state.pokemonIndex}</h5>
                            </div>
                            <div className="col-7">
                                <div className="float-right">{
                                    this.state.types.map(type => (
                                        <span key={type} className="badge badge-primary badge-pill mr-1"
                                        style={{backgroundColor: `#${TYPE_COLORS[type]}`,
                                                color: 'white'}}>
                                            {
                                                type.toLowerCase()
                                                .split(' ')
                                                .map(s => s.charAt(0).toUpperCase() + s.substring(1))
                                                .join(' ')
                                            }
                                        </span>
                                    ))}</div>
                            </div>
                        </div>
                    </div>
                    <div className="row align-items-center">
                        <div className="col-md-3">
                            <img src={this.state.imageUrl} className="card-img-top rounded mx-auto mt-2"/>
                        </div>
                        <div className="col-md-9"><h4 className="mx-auto">
                            {
                                this.state.name
                                    .toLowerCase()
                                    .split(' ')
                                    .map(s => s.charAt(0).toUpperCase() + s.substring(1)).join(" ")
                            }
                        </h4>
                            <div className="row align-items-center">
                                <div className="col-12 col-md-3">HP</div>
                                <div className="col-12 col-md-9">
                                    <div className="progress">
                                        <div className="progress-bar"
                                        style={{
                                            width: `${this.state.stats.hp}%`
                                        }}
                                             aria-valuenow="25"
                                              aria-valuemin="0"
                                              aria-valuemax="100">
                                            <small>{this.state.stats.hp}</small>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="row align-items-center">
                                <div className="col-12 col-md-3">Attack</div>
                                <div className="col-12 col-md-9">
                                    <div className="progress">
                                        <div className="progress-bar"
                                             style={{
                                                 width: `${this.state.stats.attack}%`
                                             }}
                                             aria-valuenow="25"
                                             aria-valuemin="0"
                                             aria-valuemax="100">
                                            <small>{this.state.stats.attack}</small>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="row align-items-center">
                                <div className="col-12 col-md-3">Defense</div>
                                <div className="col-12 col-md-9">
                                    <div className="progress">
                                        <div className="progress-bar"
                                             style={{
                                                 width: `${this.state.stats.defense}%`
                                             }}
                                             aria-valuenow="25"
                                             aria-valuemin="0"
                                             aria-valuemax="100">
                                            <small>{this.state.stats.defense}</small>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="row align-items-center">
                                <div className="col-12 col-md-3">Speed</div>
                                <div className="col-12 col-md-9">
                                    <div className="progress">
                                        <div className="progress-bar"
                                             style={{
                                                 width: `${this.state.stats.speed}%`
                                             }}
                                             aria-valuenow="25"
                                             aria-valuemin="0"
                                             aria-valuemax="100">
                                            <small>{this.state.stats.speed}</small>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="row align-items-center">
                                <div className="col-12 col-md-3">Special Attack</div>
                                <div className="col-12 col-md-9">
                                    <div className="progress">
                                        <div className="progress-bar"
                                             style={{
                                                 width: `${this.state.stats.specialAttack}%`
                                             }}
                                             aria-valuenow="25"
                                             aria-valuemin="0"
                                             aria-valuemax="100">
                                            <small>{this.state.stats.specialAttack}</small>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="row align-items-center">
                                <div className="col-12 col-md-3">Special Defense</div>
                                <div className="col-12 col-md-9">
                                    <div className="progress">
                                        <div className="progress-bar"
                                             style={{
                                                 width: `${this.state.stats.specialDefense}%`
                                             }}
                                             aria-valuenow="25"
                                             aria-valuemin="0"
                                             aria-valuemax="100">
                                            <small>{this.state.stats.specialDefense}</small>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="row mt-1">
                            <div className="col">
                                <p className="p-2 mr-2">{this.state.description}</p>
                            </div>
                        </div>
                    </div>
                </div>
                <hr/>
            </div>
        );
    }
}

export default Pokemon;
