import React, {Component} from 'react';

class PokemonCard extends Component {

    state = {
        name: '',
        imageUrl: '',
        pokemonIndex: ''
    };

    componentDidMount() {
        //eslint-disable-next-line
        var { url, name } = this.props;
        var pokemonIndex = url.split("/")[url.split("/").length - 2];
        var imageUrl = `https://github.com/PokeAPI/sprits/blob/master/sprites/pokemon/${pokemonIndex}.png?raw=true`;
    }

    render() {
        return (
            <div className='col-md-3 col-sm-6 mb-5'>
                <div className={'card'}>
                    <div className="card-header">
                        <h1>{this.state.name}</h1>
                    </div>
                </div>
            </div>
        );
    }
}

export default PokemonCard;
