import React, {Component} from 'react';
import { originalName as alias } from 'module'
class PokemonCard extends Component {

    state = {
        name: '',
        imageUrl: '',
        pokemonIndex: ''
    };

    componentDidMount() {
        //eslint-disable-next-line
        const { url, name } = this.props;
        const pokemonIndex = url.split("/")[url.split("/").length - 2];
        const imageUrl = `https://github.com/PokeAPI/sprits/blob/master/sprites/pokemon/${pokemonIndex}.png?raw=true`;
        this.setState({name, imageUrl, pokemonIndex});
    }

    render() {
        return (
            <div className='col-md-3 col-sm-6 mb-5'>
                <div className={'card'}>
                        <h5 className="card-header">{this.state.pokemonIndex}</h5>
                        <div className="card-body  mx-auto">
                            <h6 className={'card-title'}>{this.state.name.toLowerCase().split(" ")
                                .map(
                                    l => l.charAt(0).toUpperCase() + l.substring(1))
                                .join(" ")}
                            </h6>
                        </div>
                </div>
            </div>
        );
    }
}

export default PokemonCard;
